import Card from 'react-bootstrap/Card';
import Container from 'react-bootstrap/Container';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Placeholder from 'react-bootstrap/Placeholder';
import React, { Component } from 'react';
import UserDataService from '../api/UserDataService';
import { SESSION_USER_ID, SESSION_USER_NAME } from '../Constants';



class EmployeeManagementComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            employees: [],
        }
    }

    componentDidMount() {
        UserDataService.retrieveUser(sessionStorage.getItem(SESSION_USER_ID)).then(response => {
            UserDataService.retrieveUsersByBusiness(response.data.business.id).then(response => {
                this.setState({employees: response.data})
            })
        });
    }

    render() {
        return (
            <>
            <div style={{
                backgroundImage: "url(https://image.freepik.com/free-vector/white-faded-gradient-background-vector-with-orange-border_53876-125752.jpg"
                , backgroundPosition: 'center'
                , backgroundSize: 'cover'
                , backgroundRepeat: 'no-repeat'
                , width: '100%'
                , height: '66rem'
            }}>
                <Container>
                <Placeholder xs={12} bg="transparent"  />
                <h1  className="text-dark" >Your Employees</h1>
                <Placeholder xs={12} bg="transparent"  />
                <h3 class="text-dark ">Employees and COVID/SHN Status</h3>
                </Container>
                <Container>
                <table className="table text-dark">
                                    <thead>
                                        <tr>
                                            <th>Description</th>
                                            <th>Is Completed</th>
                                            <th>Target Date</th>
                                            <th>Update</th>
                                            <th>Delete</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {
                                            // script that for each todo, map it and display id, desc, done, targetDate, and buttons
                                            this.state.todos.map(
                                                todo =>
                                                    <tr key={todo.id}>
                                                        <td>{todo.description}</td>
                                                        <td>{todo.isDone.toString()}</td>
                                                        <td>{moment(todo.targetDate).format('YYYY-MM-DD')}</td>
                                                        <td><button className="btn btn-success" onClick={() => this.updateTodoClicked(todo.id)}>Update</button></td>
                                                        <td><button className="btn btn-warning" onClick={() => this.deleteTodoClicked(todo.id)}>Delete</button></td>
                                                    </tr>
                                            )
                                        }
                                    </tbody>
                                </table>
                </Container>
                </div>
            </>
        )
    }


}

export default EmployeeManagementComponent;
